script:
document.querySelectorAll('[id$="1"], [id$="2"], [id$="3"], [id$="4"]').forEach(botao => {
  botao.addEventListener('click', () => {
    const nome = botao.id; // exemplo: "rj2", "sp1", etc
    const url = `https://raw.githubusercontent.com/VictoriaBarel/mapa-interativo-quantica/main/cards/${nome}.png`;
    const painel = document.getElementById('painel-lateral');
    const img = document.getElementById('imagem-card');

    img.src = url;
    painel.style.display = 'flex';
  });
});
